--
-- Name: permanently_delete_entity(text, bigint); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.permanently_delete_entity(p_entity_type text, p_entity_id bigint) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_user_id uuid := auth.uid();
  v_file_path text;
BEGIN
  IF v_user_id IS NULL THEN
    RAISE EXCEPTION 'Not authenticated';
  END IF;

  CASE p_entity_type
    WHEN 'project' THEN
      DELETE FROM projects WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'asset' THEN
      DELETE FROM assets WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'sequence' THEN
      DELETE FROM sequences WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'shot' THEN
      DELETE FROM shots WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'task' THEN
      DELETE FROM tasks WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'version' THEN
      -- Get file_path before deleting so caller can clean up storage
      SELECT file_path INTO v_file_path FROM versions WHERE id = p_entity_id AND deleted_at IS NOT NULL;
      DELETE FROM versions WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'note' THEN
      DELETE FROM notes WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    WHEN 'playlist' THEN
      DELETE FROM playlists WHERE id = p_entity_id AND deleted_at IS NOT NULL;
    ELSE
      RAISE EXCEPTION 'Unknown entity type: %', p_entity_type;
  END CASE;

  RETURN jsonb_build_object(
    'success', true,
    'entity_type', p_entity_type,
    'entity_id', p_entity_id,
    'file_path', v_file_path
  );
END;
$$;


--
-- Name: restore_entity(text, bigint); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.restore_entity(p_entity_type text, p_entity_id bigint) RETURNS jsonb
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_user_id uuid := auth.uid();
  v_project_id bigint;
  v_deleted_at timestamptz;
BEGIN
  IF v_user_id IS NULL THEN
    RAISE EXCEPTION 'Not authenticated';
  END IF;

  CASE p_entity_type
    WHEN 'project' THEN
      SELECT deleted_at INTO v_deleted_at FROM projects WHERE id = p_entity_id;
      IF v_deleted_at IS NULL THEN
        RAISE EXCEPTION 'Entity is not trashed';
      END IF;
      -- Restore project
      UPDATE projects SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;
      -- Restore children trashed at the same time (within 2-second window)
      UPDATE assets SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE sequences SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE shots SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE tasks SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE versions SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE notes SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      UPDATE playlists SET deleted_at = NULL, deleted_by = NULL
        WHERE project_id = p_entity_id
        AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';

    WHEN 'asset' THEN
      SELECT project_id INTO v_project_id FROM assets WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE assets SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    WHEN 'sequence' THEN
      SELECT project_id INTO v_project_id FROM sequences WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE sequences SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    WHEN 'shot' THEN
      SELECT project_id INTO v_project_id FROM shots WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE shots SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    WHEN 'task' THEN
      SELECT project_id INTO v_project_id FROM tasks WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE tasks SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    WHEN 'version' THEN
      SELECT project_id INTO v_project_id FROM versions WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE versions SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    WHEN 'note' THEN
      SELECT project_id, deleted_at INTO v_project_id, v_deleted_at FROM notes WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE notes SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;
      -- Restore cascade-trashed child notes
      IF v_deleted_at IS NOT NULL THEN
        UPDATE notes SET deleted_at = NULL, deleted_by = NULL
          WHERE parent_note_id = p_entity_id
          AND deleted_at BETWEEN v_deleted_at - interval '2 seconds' AND v_deleted_at + interval '2 seconds';
      END IF;

    WHEN 'playlist' THEN
      SELECT project_id INTO v_project_id FROM playlists WHERE id = p_entity_id;
      IF EXISTS (SELECT 1 FROM projects WHERE id = v_project_id AND deleted_at IS NOT NULL) THEN
        RAISE EXCEPTION 'Cannot restore: parent project is trashed. Restore the project first.';
      END IF;
      UPDATE playlists SET deleted_at = NULL, deleted_by = NULL WHERE id = p_entity_id;

    ELSE
      RAISE EXCEPTION 'Unknown entity type: %', p_entity_type;
  END CASE;

  RETURN jsonb_build_object(
    'success', true,
    'entity_type', p_entity_type,
    'entity_id', p_entity_id
  );
END;
$$;


--
-- Name: schema_add_field_to_entity(bigint, text, boolean, boolean, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.schema_add_field_to_entity(p_field_id bigint, p_entity_type text, p_required boolean DEFAULT false, p_visible_by_default boolean DEFAULT true, p_display_order integer DEFAULT 1000) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'pg_temp'
    AS $$
DECLARE
  v_actor uuid;
  v_field public.schema_fields%ROWTYPE;
  v_entity_type text;
  v_table_name text;
  v_column_def text;
  v_exists boolean;
BEGIN
  v_actor := public.schema_require_field_admin();
  v_entity_type := lower(trim(coalesce(p_entity_type, '')));

  SELECT *
  INTO v_field
  FROM public.schema_fields sf
  WHERE sf.id = p_field_id;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Field % not found', p_field_id;
  END IF;

  IF v_field.is_active = false THEN
    RAISE EXCEPTION 'Field % is inactive', p_field_id;
  END IF;

  v_table_name := public.schema_entity_table_name(v_entity_type);
  IF v_table_name IS NULL THEN
    RAISE EXCEPTION 'Unsupported entity type: %', v_entity_type;
  END IF;

  v_column_def := public.schema_column_definition_for_data_type(v_field.data_type);

  SELECT EXISTS (
    SELECT 1
    FROM information_schema.columns c
    WHERE c.table_schema = 'public'
      AND c.table_name = v_table_name
      AND c.column_name = v_field.code
  )
  INTO v_exists;

  IF NOT v_exists THEN
    EXECUTE format(
      'ALTER TABLE public.%I ADD COLUMN IF NOT EXISTS %I %s',
      v_table_name,
      v_field.code,
      v_column_def
    );
  END IF;

  INSERT INTO public.schema_field_entities (
    field_id,
    entity_type,
    table_name,
    column_name,
    required,
    visible_by_default,
    display_order,
    is_active,
    created_by,
    updated_by
  )
  VALUES (
    p_field_id,
    v_entity_type,
    v_table_name,
    v_field.code,
    p_required,
    p_visible_by_default,
    coalesce(p_display_order, 1000),
    true,
    v_actor,
    v_actor
  )
  ON CONFLICT (field_id, entity_type)
  DO UPDATE SET
    required = EXCLUDED.required,
    visible_by_default = EXCLUDED.visible_by_default,
    display_order = EXCLUDED.display_order,
    is_active = true,
    updated_by = v_actor,
    updated_at = now();

  INSERT INTO public.schema_change_log (action, field_id, actor_id, payload)
  VALUES (
    'add_field_to_entity',
    p_field_id,
    v_actor,
    jsonb_build_object('entity_type', v_entity_type)
  );
END;
$$;


--
-- Name: schema_bootstrap_table_columns(text); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.schema_bootstrap_table_columns(p_entity_type text DEFAULT NULL::text) RETURNS integer
    LANGUAGE plpgsql SECURITY DEFINER
    SET search_path TO 'public', 'pg_temp'
    AS $$
DECLARE
  v_actor uuid;
  v_target_entity text := nullif(lower(trim(coalesce(p_entity_type, ''))), '');
  v_entity record;
  v_col record;
  v_field_id bigint;
  v_data_type text;
  v_count integer := 0;
BEGIN
  v_actor := public.schema_require_field_admin();

  FOR v_entity IN
    SELECT *
    FROM (
      VALUES
        ('asset'::text, 'assets'::text),
        ('sequence'::text, 'sequences'::text),
        ('shot'::text, 'shots'::text),
        ('task'::text, 'tasks'::text),
        ('version'::text, 'versions'::text),
        ('note'::text, 'notes'::text),
        ('published_file'::text, 'published_files'::text),
        ('project'::text, 'projects'::text),
        ('department'::text, 'departments'::text),
        ('person'::text, 'profiles'::text)
    ) AS entities(entity_type, table_name)
  LOOP
    IF v_target_entity IS NOT NULL AND v_entity.entity_type <> v_target_entity THEN
      CONTINUE;
    END IF;

    FOR v_col IN
      SELECT
        c.column_name,
        c.data_type,
        c.udt_name
      FROM information_schema.columns c
      WHERE c.table_schema = 'public'
        AND c.table_name = v_entity.table_name
        AND c.column_name <> ALL (
          ARRAY[
            'id',
            'project_id',
            'entity_type',
            'entity_id',
            'created_at',
            'updated_at',
            'created_by',
            'updated_by'
          ]
        )
      ORDER BY c.ordinal_position
    LOOP
      IF EXISTS (
        SELECT 1
        FROM public.schema_field_entities sfe
        WHERE sfe.entity_type = v_entity.entity_type
          AND sfe.column_name = v_col.column_name
      ) THEN
        CONTINUE;
      END IF;

      v_data_type := public.schema_data_type_from_db_column(v_col.data_type, v_col.udt_name);

      INSERT INTO public.schema_fields (
        code,
        name,
        data_type,
        field_type,
        description,
        is_active,
        created_by,
        updated_by
      )
      VALUES (
        v_col.column_name,
        initcap(replace(v_col.column_name, '_', ' ')),
        v_data_type,
        'dynamic',
        'Bootstrapped from existing DB column',
        true,
        v_actor,
        v_actor
      )
      ON CONFLICT (code)
      DO UPDATE SET
        updated_by = v_actor,
        updated_at = now()
      RETURNING id INTO v_field_id;

      INSERT INTO public.schema_field_entities (
        field_id,
        entity_type,
        table_name,
        column_name,
        required,
        visible_by_default,
        display_order,
        is_active,
        created_by,
        updated_by
      )
      VALUES (
        v_field_id,
        v_entity.entity_type,
        v_entity.table_name,
        v_col.column_name,
        false,
        true,
        1000,
        true,
        v_actor,
        v_actor
      )
      ON CONFLICT (field_id, entity_type)
      DO UPDATE SET
        is_active = true,
        updated_by = v_actor,
        updated_at = now();

      v_count := v_count + 1;
    END LOOP;
  END LOOP;

  INSERT INTO public.schema_change_log (action, actor_id, payload)
  VALUES (
    'bootstrap_fields',
    v_actor,
    jsonb_build_object('entity_type', v_target_entity, 'inserted_or_linked', v_count)
  );

  RETURN v_count;
END;
$$;


