  IF v_code !~ '^[a-z][a-z0-9_]{0,62}$' THEN
    RAISE EXCEPTION 'Invalid field code. Use lowercase letters, numbers, underscore, and start with a letter.';
  END IF;

  IF v_code = ANY (
    ARRAY[
      'id',
      'project_id',
      'entity_type',
      'entity_id',
      'created_at',
      'updated_at',
      'created_by',
      'updated_by'
    ]
  ) THEN
    RAISE EXCEPTION 'Field code % is reserved', v_code;
  END IF;

  RETURN v_code;
END;
$_$;


